<script>
  // Smooth scroll and remove hash from URL without jumping
  function smoothScrollWithoutHash(event) {
    const href = event.currentTarget.getAttribute('href');
    if (href && href.startsWith('#')) {
      event.preventDefault();

      const targetId = href.substring(1);
      const targetElem = document.getElementById(targetId);
      if (targetElem) {
        targetElem.scrollIntoView({ behavior: 'smooth' });

        // Remove hash from URL after scrolling without reloading
        history.replaceState(null, '', window.location.pathname + window.location.search);
      }
    }
  }

  // Attach to all nav links that have hash hrefs
  document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', smoothScrollWithoutHash);
  });
</script>